"use strict";

const vscode = require("vscode");

const dirs  = require("./operation/dirs");
const files = require("./operation/files");

const prjs    = require("./operation/prjs");
const monitor = require("./moniter/moniter");

exports.dirs    = dirs;
exports.files   = files;
exports.prjs    = prjs;
exports.monitor = monitor;

function registerPrjsServer(context, opeParam){
    context.subscriptions.push(vscode.commands.registerCommand('TOOL.Gen_Property', () => {
        prjs.generatePropertyFile(opeParam);
    }));
    context.subscriptions.push(vscode.commands.registerCommand('TOOL.Overwrite_InitProperty', () => {
        prjs.overwriteInitProperty(opeParam);
    }));
    context.subscriptions.push(vscode.commands.registerCommand('HARD.Add_dev', () => {
        prjs.addDevice(opeParam);
    }));
    context.subscriptions.push(vscode.commands.registerCommand('HARD.Delete_dev', () => {
        prjs.delDevice(opeParam);
    }));
}
exports.registerPrjsServer = registerPrjsServer;