"use strict";

const fsm_vhdl = require('./fsm_vhdl');
const fsm_volg = require('./fsm_vlog');

function get_svg_sm(language, code, comment_symbol) {
    if (language === "vhdl") {
        var parser = new fsm_vhdl.Paser_fsm_vhdl(comment_symbol);
    }
    else if (language === "verilog" || language === "systemverilog") {
        var parser = new fsm_volg.Paser_fsm_vlog(comment_symbol);
    }
    let stm = parser.get_svg_sm(code);
    return stm;
}
exports.get_svg_sm = get_svg_sm;
