function registeHardServer(context, indexer, opeParam, fileExplorer) {
    let netlist_viewer_manager = new build.synthNetlist(context, opeParam, indexer.HDLparam);
    vscode.commands.registerCommand('TOOL.netlist.viewer', (uri) => {
        netlist_viewer_manager.open_viewer(uri);
    });
    vscode.commands.registerCommand('TOOL.netlist', (uri) => {
        netlist_viewer_manager.open_viewer(uri);
    });

    let xilinxBuild = new build.hardwareRegister(context, opeParam);
    vscode.commands.registerCommand('HARD.Launch', () => {
        xilinxBuild.Launch();
    });
    vscode.commands.registerCommand('HARD.Refresh', () => {
        xilinxBuild.Refresh();
    });
    vscode.commands.registerCommand('HARD.srcTop', (uri) => {
        xilinxBuild.setSrcTop(uri, indexer.HDLparam, fileExplorer);
    });
    vscode.commands.registerCommand('HARD.simTop', (uri) => {
        xilinxBuild.setSimTop(uri, indexer.HDLparam, fileExplorer);
    });
    vscode.commands.registerCommand('HARD.Simulate', () => {
        xilinxBuild.Simulation();
    });
    vscode.commands.registerCommand('HARD.Build', () => {
        xilinxBuild.Build();
    });
    vscode.commands.registerCommand('HARD.Synth', () => {
        xilinxBuild.Synth();
    });
    vscode.commands.registerCommand('HARD.Impl', () => {
        xilinxBuild.Impl();
    });
    vscode.commands.registerCommand('HARD.Gen_Bit', () => {
        xilinxBuild.Gen_Bit();
    });
    vscode.commands.registerCommand('HARD.Program', () => {
        xilinxBuild.Program();
    });
    vscode.commands.registerCommand('HARD.GUI', () => {
        xilinxBuild.GUI();
    });
    vscode.commands.registerCommand('HARD.Exit', () => {
        xilinxBuild.Exit();
    });
}
exports.registeHardServer = registeHardServer;

